import React from "react";


export class NewTicketComponent extends React.PureComponent{
    constructor(props){
        super(props);
        this.state = {
            info:JSON.parse(sessionStorage.getItem("project1_data")),
            data:JSON.parse(sessionStorage.getItem("userData")),
            reimbType:"1",
            description:"",
            reimbAmount:"0"
        }
        this.auth = document.cookie.split("=")[1]

    }

    capitalizeFirstLetter(string) {
        return string.charAt(0).toUpperCase() + string.slice(1);
    }

    createNewTicket =(event) => {
        event.preventDefault();
        console.log(event.target.value)
    }

    updateReimbursementType =(event) =>{
        console.log(event.target)
        // this.setState({
        //     ...this.state,
        //     reimbType:event.target.value
        // })
        /**
         * 1 is for lodging
         * 2 is for travel
         * 3 is for food
         * 4 is for other
         */
        /**
         * amount,
         * time stamp is on the server side
         * description
         * userRoleID
         * 1 is for progress
         */
    }

    render(){
        console.log(this.state);
        return(
            <>
                <div>
                    <form onSubmit={this.createNewTicket}>
                        <div class="form-group">
                            <label for="reimbursementType">Reimbursement Type</label>
                            <select class="form-control" id="reimbursementType" onChange={this.updateReimbursementType}>
                            <option alue="1" >Lodging</option>
                            <option defaultValue="2" >Travel</option>
                            <option defaultValue="3">Food</option>
                            <option defaultValue="4">Other</option>
                            </select>
                        </div>
                        <div class="form-group">
                            <label for="exampleFormControlTextarea1">Description</label>
                            <textarea maxLength="250" class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
                            <label for="reimbursementAmount">Amount</label>
                            <textarea  class="form-control" id="reimbursementAmount" rows="3"></textarea>
                        </div>
                    </form>
                </div>
            </>
        )
    }
}
