import React from "react";
// import "../main.css";
export class LogInComponent extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            username: "",
            password: ""
        }
        this.hash = require("hash.js");
        // console.log(this.hash.sha256("hello").digest("hex"));
    }


    passwordChange = (e) =>{
        this.setState({
            ...this.state,
            password: this.hash.sha256(e.target.value).digest("hex")
        })
    }


    submit = (e) =>{
        e.preventDefault();
    }


    render(){
        return(
            <>
            <div className="login_class">
                <header>
                    <img src="https://3g4d13k75x47q7v53surz1gi-wpengine.netdna-ssl.com/wp-content/themes/revature/imgs/logo.png"></img>
                </header>
                <div class="login_div">
                    <h2 id="sign_h2">Sign in</h2>
                    <form id="login_form" method="POST">
                        <label id="username_label">Email:</label> <input type="text" name="username" required autocomplete="off" value="joe.antonio.perez@gmail.com"/>
                        <br/>
                        <label id="password_label">Password:</label> <input type="password" name="password" required  autocomplete="off" value="some password"/>
                        <br/>
                        <button id="submit_login">Log In</button>
                        <button id="submit_forget">Forgot Password</button>
                        <br/>
                        <label id="login_error"/>
                    </form>
                </div>
            </div>
            </>
        )
    }
}
