import React from "react";


export class NewTicketComponent extends React.PureComponent{
    constructor(props){
        super(props);
        this.state = {
            info:JSON.parse(sessionStorage.getItem("project1_data")),
            data:JSON.parse(sessionStorage.getItem("userData")),
            reimbType:"1",
            description:"",
            reimbAmount:"0"
        }
        this.auth = document.cookie.split("=")[1]

    }

    capitalizeFirstLetter(string) {
        return string.charAt(0).toUpperCase() + string.slice(1);
    }

    createNewTicket =(event) => {
        event.preventDefault();
        console.log("submitting data");
        let data = JSON.stringify({
            "userID": this.state.info.userID,
            "description":this.state.description,
            "reimbursementAmount":this.state.reimbAmount,
            "reimbursementType":this.state.reimbType
        })
        console.log(data);
        fetch("http://localhost:8080/project1/newticket",{
            headers:{
                "Content-Type":"application/x-www-form-urlencoded"
            },
            method:"post",
            body:data
        })
    }

    updateReimbursementType =(event) =>{
        this.setState({
            ...this.state,
            reimbType:event.target.value
        })

    }
    updateDescription = (event)=>{
        this.setState({
            ...this.state,
            description:event.target.value
        })
    }

    updateAmount = (event) =>{
        this.setState({
            ...this.state,
            reimbAmount:event.target.value
        })
    }

    render(){
        return(
            <>
                <div className="new_ticket_">
                    <form onSubmit={this.createNewTicket}>
                        <div class="form-group">
                            <label for="reimbursementType">Reimbursement Type</label>
                            <select class="form-control" id="reimbursementType" onChange={this.updateReimbursementType}>
                            <option value="1" >Lodging</option>
                            <option value="2" >Travel</option>
                            <option value="3">Food</option>
                            <option value="4">Other</option>
                            </select>
                        </div>
                        <div class="form-group">
                            <label for="description">Description</label>
                            <textarea required maxLength="250" class="form-control" id="description" onChange={this.updateDescription} rows="3"></textarea>
                            <label for="reimbursementAmount">Amount</label>
                            <input type="number" required  class="form-control" id="reimbursementAmount" onChange={this.updateAmount} rows="3"/>
                        </div>
                        <button>Submit</button>
                    </form>
                </div>
            </>
        )
    }
}
