import React from "react";
import classNames from 'classnames';
import {FaBed} from "react-icons/fa";



export class FinanceRenderComponent extends React.Component{
    // "http://localhost:8080/project1/status"
    constructor(props){
        super(props);
        this.state={
            status:this.props.ticket.reimbursementStatus
        }
    }
    acceptInvoice = (event) =>{
        console.log(event.target.value);
        let data = JSON.stringify({
            "auth":document.cookie.split("=")[1],
            "newStatus": 2,
            "reimb_id": event.target.value
        });
        console.log(data);
        fetch("http://localhost:8080/project1/status",{
            method:"POST",
            body: data
        }).then(response=>{
            console.log(response.status);
        })
        this.setState({
            ...this.state,
            status:"approved"
        })

    }
    rejectInvoice = (event) =>{
        console.log(event.target);
        let data = JSON.stringify({
            "auth":document.cookie.split("=")[1],
            "newStatus": 3,
            "reimb_id": event.target.value
        })
        fetch("http://localhost:8080/project1/status",{
            method:"POST",
            body: data
        }).then(response=>{
            console.log(response.status);
        })
        this.setState({
            ...this.state,
            status:"denied"
        })

    }
    getIcon = (t) =>{

    }

    render(){
        let icon = get
        return(
            <>
            <tr className={this.state.status} >
                <td>{this.props.ticket.reimbursementID}</td>
                <td>{this.props.ticket.description}</td>
                <td>${this.props.ticket.reimbursementAmount}</td>
                <td><FaBed/></td>
                {/* <td>{this.props.ticket.reimbursementType}</td> */}
                <td>{this.state.status}</td>
                <td>{new Date(this.props.ticket.submitted).toLocaleDateString()}</td>
                <td>{this.props.ticket.userRole}</td>
                <td><button className="btn btn-success"  value={this.props.ticket.reimbursementID} onClick={this.acceptInvoice}>Accept</button></td>
                <td><button className="btn btn-danger"  value={this.props.ticket.reimbursementID} onClick={this.rejectInvoice} >Reject</button></td>
            </tr>
            </>
        )
    }
}
