import React from "react";
import classNames from 'classnames';
import {FaBed} from "react-icons/fa";
import {TiPlane} from "react-icons/ti";
import {MdLocalPizza} from "react-icons/md";
import { IoIosCheckmarkCircleOutline } from "react-icons/io";
import { FaStopCircle } from "react-icons/fa";
import { FiPlay } from "react-icons/fi";



export class FinanceRenderComponent extends React.Component{
    constructor(props){
        super(props);
        this.state={
            status:this.props.ticket.reimbursementStatus
        }
    }
    acceptInvoice = (event) =>{
        let data = JSON.stringify({
            "auth":document.cookie.split("=")[1],
            "newStatus": 2,
            "reimb_id": event.target.value
        });
        fetch("http://localhost:8080/project1/status",{
            method:"POST",
            body: data
        })
        this.setState({
            ...this.state,
            status:"approved"
        })
        console.log(this.state);

    }
    rejectInvoice = (event) =>{
        let data = JSON.stringify({
            "auth":document.cookie.split("=")[1],
            "newStatus": 3,
            "reimb_id": event.target.value
        })
        fetch("http://localhost:8080/project1/status",{
            method:"POST",
            body: data
        }).then(response=>{
            console.log(response.status);
        })
        this.setState({
            ...this.state,
            status:"denied"
        });
        console.log(this.state);


    }

    changeInvoiceStatus = (event) =>{
        let data = JSON.parse
        console.log();
        console.log(event.target.idValue);

    }


    getIcon(t){
        if(t == "lodging"){
            return <FaBed/>
        }
        if(t == "travel"){
            return <TiPlane/>
        }
        if (t == "food"){
            return <MdLocalPizza/>
        }
    }

    getStatusIcon(t){

        if(t == "pending"){
            return <FiPlay/>
        }
        if(t == "approved"){
            return <IoIosCheckmarkCircleOutline/>
        }
        if (t == "denied"){
            return <FaStopCircle/>
        }
    }


    render(){
        let icon = this.getIcon(this.props.ticket.reimbursementType)
        return(
            <>
            <tr className={this.state.status} >
                <td>{this.props.ticket.reimbursementID}</td>
                <td>{this.props.ticket.description}</td>
                <td>${this.props.ticket.reimbursementAmount}</td>
                <td>{icon}</td>
                <td>{this.getStatusIcon(this.state.status)}</td>
                <td>{new Date(this.props.ticket.submitted).toLocaleDateString()}</td>
                <td>{this.props.ticket.userRole}</td>
                <td><button className="btn btn-success" id="{status:approve, value:2}" idvalue="2"  value={this.props.ticket.reimbursementID} onClick={this.changeInvoiceStatus}>Accept</button></td>
                <td><button className="btn btn-danger" id="denied" idvalue="3" value={this.props.ticket.reimbursementID} onClick={this.rejectInvoice} >Reject</button></td>
            </tr>
            </>
        )
    }
}
