import React from "react";
import classNames from 'classnames';
import {FaBed} from "react-icons/fa";
import {TiPlane} from "react-icons/ti";
import {MdLocalPizza} from "react-icons/md";
import { IoIosCheckmarkCircleOutline } from "react-icons/io";
import { FaStopCircle } from "react-icons/fa";
import { FiPlay } from "react-icons/fi";



export class FinanceRenderComponent extends React.Component{
    constructor(props){
        super(props);
        this.state={
            status:this.props.ticket.reimbursementStatus
        }
    }
    acceptInvoice = (event) =>{
        let data = JSON.stringify({
            "auth":document.cookie.split("=")[1],
            "newStatus": 2,
            "reimb_id": event.target.value
        });
        fetch("http://localhost:8080/project1/status",{
            method:"POST",
            body: data
        })
        this.setState({
            ...this.state,
            status:"approved"
        })
        console.log(this.state);

    }
    rejectInvoice = (event) =>{
        let data = JSON.stringify({
            "auth":document.cookie.split("=")[1],
            "newStatus": 3,
            "reimb_id": event.target.value
        })
        fetch("http://localhost:8080/project1/status",{
            method:"POST",
            body: data
        }).then(response=>{
            console.log(response.status);
        })
        this.setState({
            ...this.state,
            status:"denied"
        });
        console.log(this.state);


    }

    changeInvoiceStatus = (event) =>{
        let statuses = event.target.id.split(",");
        let data = JSON.stringify({
            "auth":document.cookie.split("=")[1],
            "newStatus": parseInt(statuses[1]),
            "reimb_id": event.target.value
        })
        console.log(event.target.idValue);
