        if(t == "lodging"){
            return <FaBed/>
        }
        if(t == "travel"){
            return <TiPlane/>
        }
        if (t == "food"){
            return <MdLocalPizza/>
        }
    }

    getStatusIcon(t){

        if(t == "pending"){
            return <FiPlay/>
        }
        if(t == "approved"){
            return <IoIosCheckmarkCircleOutline/>
        }
        if (t == "denied"){
            return <FaStopCircle/>
        }
    }


    render(){
        let icon = this.getIcon(this.props.ticket.reimbursementType)
        return(
            <>
            <tr className={this.state.status} >
                <td>{this.props.ticket.reimbursementID}</td>
                <td>{this.props.ticket.description}</td>
                <td>${this.props.ticket.reimbursementAmount}</td>
                <td>{icon}</td>
                <td>{this.getStatusIcon(this.state.status)}</td>
                <td>{new Date(this.props.ticket.submitted).toLocaleDateString()}</td>
                <td>{this.props.ticket.userRole}</td>
                <td><button className="btn btn-success" id={["approve",2]}  value={this.props.ticket.reimbursementID} onClick={this.changeInvoiceStatus}>Accept</button></td>
                <td><button className="btn btn-danger" id={["denied",3]}  value={this.props.ticket.reimbursementID} onClick={this.rejectInvoice} >Reject</button></td>
            </tr>
            </>
        )
    }
}
