import React from "react";
import {IoIosPersonAdd} from "react-icons/io";
import {IoIosLogOut} from "react-icons/io";
import { AdminHelperUsersComponent } from "./admin_helper_users.components";
import { EmployeeRender } from "./Renders/employee.renderer";


export class EmployeeComponent extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            info:JSON.parse(sessionStorage.getItem("project1_data")),
            data:JSON.parse(sessionStorage.getItem("userData"))
        }
        if (this.state.info == null ){
            document.location.href = "/index";
        }
        this.auth = document.cookie.split("=")[1]
    }

    componentDidMount(){
        console.log(this.state.info);
        console.log(this.auth);
        console.log(this.state.data);

    }
    capitalizeFirstLetter(string) {
        return string.charAt(0).toUpperCase() + string.slice(1);
    }

    userLogOut = (event)=>{
        event.preventDefault();
        fetch(`http://localhost:8080/project1/logout`,{
            headers:{
                "Content-Type":"application/x-www-form-urlencoded"
            },
            method:"post",
            body:JSON.stringify({"auth":event.target.value})
        })
        this.setState({
            ...this.state,
            data:null
        });
        sessionStorage.removeItem("project1_data");
        document.location.href="/index";

    }
    newTicket = (event)=>{
        console.log(event);
        this.buttonName = "Submit"
    }

    render(){
        let buttonName = "New"
        return (
            <div>
                <nav className="navbar navbar-expand-lg navbar-light bg-light">

                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarTogglerDemo03" aria-controls="navbarTogglerDemo03" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                    </button>
                    <a className="navbar-brand">Hello {this.capitalizeFirstLetter(this.state.info.firstName)}</a>

                    <div className="collapse navbar-collapse" id="navbarTogglerDemo03">
                    <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
                        <li className="nav-item active">
                            <a className="nav-link" href="#">Home <span className="sr-only">(current)</span></a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" value={this.auth} onClick={this.userLogOut} title="Log Out">Log Out</a>
                        </li>
                    </ul>
                    </div>
                </nav>
                <table className="table table-dark table-bordered table-hover">
                    <tbody>
                        <tr>
                            <th>Reimbursement ID</th>
                            <th>Description</th>
                            <th>Amount</th>
                            <th>Type</th>
                            <th>Status</th>
                            <th>Role</th>
                        </tr>
                    </tbody>
                    {
                        this.state.data.map(tickets=>(
                            <EmployeeRender key={tickets.reimbursementID} ticket={tickets}/>
                        ))
                    }
                </table>
                <button className="btn btn-player" onClick={this.newTicket} > {this.buttonName}</button>

                {/* {
                    this.state.data
                } */}

            </div>
        )
    }
}
